import React from 'react';

class SignIn extends React.Component{
    constructor(props){
        super();
        this.state = {
            signInEmail: '',
            signInPassword: ''
        }
    }
    onEmailChange = (event) => {
        this.setState({signInEmail: event.target.value})
    }
    onPasswordChange = (event) => {
        this.setState({signInPassword: event.target.value})
    }

    onSubmitSignIn = () => {
        fetch('http://localhost:3000/signin', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                email: this.state.signInEmail,
                password: this.state.signInPassword
            })
        })
        .then(response => response.json())
        .then(data => {
            if (data != 'error'){
                this.props.onRouteChange('home');
            }
        })
    }

    render() {
        const {onRouteChange} = this.props;
        return (
            <article className="br3 ba b--black-10 mv6 w-100 w-50-m w-25-l mw5 center shadow-5">
                <main className="pa4 black-80">
        <form className="measure">
            <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
            <legend class="f1 fw6 ph0 mh0">Sign In</legend>
            <div class="mt3">
                <label className="db fw6 lh-copy f6" for="email-address">Email</label>
                <input onChange={this.onEmailChange}
                className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="email" name="email-address"  id="email-address"/>
            </div>
            <div className="mv3">
                <label className="db fw6 lh-copy f6" for="password">Password</label>
                <input 
                onChange={this.onPasswordChange}
                className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password"  id="password"/>
            </div>
            </fieldset>
            <div className="">
            <input 
            onClick={this.onSubmitSignIn}
            className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
            type="submit" 
            value="Sign in"/>
            </div>
            <div className="lh-copy mt3">
            <p onClick={() => onRouteChange("register")} className="pointer f6 link dim black db">Register</p>
            </div>
        </form>
        </main>
        </article>
        );
    }
}
export default SignIn;
